stylesheet:
QWidget {
    background-color: #2e2e2e;
    color: #e0e0e0;
}
QPushButton {
    background-color: #424242;
    border: 1px solid #616161;
    padding: 5px;
    border-radius: 3px;
}
QPushButton:hover {
    background-color: #757575;
}
QPushButton:pressed {
    background-color: #505050;
}
QPushButton:disabled {
    background-color: #505050;
    color: #616161;
}
QFrame {
    border: 1px solid #424242;
    border-radius: 4px;
}
QComboBox {
    background-color: #424242;
    border: 1px solid #616161;
    border-radius: 3px;
    padding: 3px;
}
QComboBox::drop-down {
    border-left: 1px solid #616161;
}
QComboBox QAbstractItemView {
    background-color: #424242;
    border: 1px solid #616161;
    selection-background-color: #757575;
}
QDoubleSpinBox {
    background-color: #424242;
    border: 1px solid #616161;
    border-radius: 3px;
    padding: 3px;
}
QLabel {
    background-color: transparent;
    border: none;
}
#total_weight_label {
    color: #ff8f00;
}
#status_label {
    color: #ff8f00;
    border-top: 1px solid #424242;
}
QScrollArea {
    border: none;
}
QScrollBar:vertical {
    background: #2e2e2e;
    width: 12px;
    margin: 0px;
    border-radius: 6px;
}
QScrollBar::handle:vertical {
    background: #424242;
    min-height: 20px;
    border-radius: 6px;
}
QScrollBar::handle:vertical:hover {
    background: #616161;
}
QScrollBar::add-line:vertical, QScrollBar::sub-line:vertical {
    background: none;
    height: 0px;
    subcontrol-position: top;
    subcontrol-origin: margin;
}
