stylesheet:
QWidget {
    background-color: #1c1c1c;
    color: #c0c0c0;
}
QPushButton {
    background-color: #333333;
    border: 1px solid #4a4a4a;
    padding: 5px;
    border-radius: 3px;
}
QPushButton:hover {
    background-color: #555555;
}
QPushButton:pressed {
    background-color: #2a2a2a;
}
QPushButton:disabled {
    background-color: #2a2a2a;
    color: #4a4a4a;
}
QFrame {
    border: 1px solid #333333;
    border-radius: 4px;
}
QComboBox {
    background-color: #333333;
    border: 1px solid #4a4a4a;
    border-radius: 3px;
    padding: 3px;
}
QComboBox::drop-down {
    border-left: 1px solid #4a4a4a;
}
QComboBox QAbstractItemView {
    background-color: #333333;
    border: 1px solid #4a4a4a;
    selection-background-color: #555555;
}
QDoubleSpinBox {
    background-color: #333333;
    border: 1px solid #4a4a4a;
    border-radius: 3px;
    padding: 3px;
}
QLabel {
    background-color: transparent;
    border: none;
}
#total_weight_label {
    color: #d6c8e0;
}
#status_label {
    color: #d6c8e0;
    border-top: 1px solid #333333;
}
QScrollArea {
    border: none;
}
QScrollBar:vertical {
    background: #1c1c1c;
    width: 12px;
    margin: 0px;
    border-radius: 6px;
}
QScrollBar::handle:vertical {
    background: #333333;
    min-height: 20px;
    border-radius: 6px;
}
QScrollBar::handle:vertical:hover {
    background: #4a4a4a;
}
QScrollBar::add-line:vertical, QScrollBar::sub-line:vertical {
    background: none;
    height: 0px;
    subcontrol-position: top;
    subcontrol-origin: margin;
}
