stylesheet:
QWidget {
    background-color: #282a36;
    color: #f8f8f2;
}
QPushButton {
    background-color: #44475a;
    border: 1px solid #6272a4;
    padding: 5px;
    border-radius: 3px;
}
QPushButton:hover {
    background-color: #4f5368;
}
QPushButton:pressed {
    background-color: #3b3e51;
}
QPushButton:disabled {
    background-color: #3b3e51;
    color: #6272a4;
}
QFrame {
    border: 1px solid #44475a;
    border-radius: 4px;
}
QComboBox {
    background-color: #44475a;
    border: 1px solid #6272a4;
    border-radius: 3px;
    padding: 3px;
}
QComboBox::drop-down {
    border-left: 1px solid #6272a4;
}
QComboBox QAbstractItemView {
    background-color: #44475a;
    border: 1px solid #6272a4;
    selection-background-color: #6272a4;
}
QDoubleSpinBox {
    background-color: #44475a;
    border: 1px solid #6272a4;
    border-radius: 3px;
    padding: 3px;
}
QLabel {
    background-color: transparent;
    border: none;
}
#total_weight_label {
    color: #50fa7b;
}
#status_label {
    color: #bd93f9;
    border-top: 1px solid #44475a;
}
QScrollArea {
    border: none;
}
QScrollBar:vertical {
    background: #282a36;
    width: 12px;
    margin: 0px;
    border-radius: 6px;
}
QScrollBar::handle:vertical {
    background: #44475a;
    min-height: 20px;
    border-radius: 6px;
}
QScrollBar::handle:vertical:hover {
    background: #6272a4;
}
QScrollBar::add-line:vertical, QScrollBar::sub-line:vertical {
    background: none;
    height: 0px;
    subcontrol-position: top;
    subcontrol-origin: margin;
}
