stylesheet:
QWidget {
    background-color: #2e2e2e;
    color: #e8e6f0;
}
QPushButton {
    background-color: #3f3f3f;
    border: 2px solid #6fff00;
    padding: 5px;
    border-radius: 2px;
}
QPushButton:hover {
    background-color: #555555;
}
QPushButton:pressed {
    background-color: #333333;
}
QPushButton:disabled {
    background-color: #333333;
    color: #666666;
    border: 1px solid #4a406b;
}
QFrame {
    background-color: #4a406b;
    border: 1px solid #7d40c0;
    border-radius: 2px;
}
QComboBox {
    background-color: #2e2e2e;
    border: 1px solid #7d40c0;
    border-radius: 2px;
    padding: 3px;
}
QComboBox::drop-down {
    border-left: 1px solid #7d40c0;
}
QComboBox QAbstractItemView {
    background-color: #4a406b;
    border: 1px solid #7d40c0;
    selection-background-color: #3b315c;
}
QDoubleSpinBox {
    background-color: #2e2e2e;
    border: 1px solid #7d40c0;
    border-radius: 2px;
    padding: 3px;
}
QLabel {
    background-color: transparent;
    border: none;
}
#total_weight_label {
    color: #6fff00;
}
#status_label {
    color: #e8e6f0;
    border-top: 1px solid #7d40c0;
}
QScrollArea {
    border: none;
}
QScrollBar:vertical {
    background: #2e2e2e;
    width: 12px;
    margin: 0px;
    border-radius: 6px;
}
QScrollBar::handle:vertical {
    background: #f8a102;
    min-height: 20px;
    border-radius: 6px;
}
QScrollBar::handle:vertical:hover {
    background: #ffc25A;
}
QScrollBar::add-line:vertical, QScrollBar::sub-line:vertical {
    background: none;
    height: 0px;
    subcontrol-position: top;
    subcontrol-origin: margin;
}
