stylesheet:
QWidget {
    background-color: #1a1e26;
    color: #f0f0f0;
}
QPushButton {
    background-color: #2b3340;
    border: 1px solid #4a5d73;
    padding: 5px;
    border-radius: 3px;
}
QPushButton:hover {
    background-color: #3b4759;
}
QPushButton:pressed {
    background-color: #20262e;
}
QPushButton:disabled {
    background-color: #252c36;
    color: #5a6e84;
}
QFrame {
    border: 1px solid #4a5d73;
    border-radius: 4px;
}
QComboBox {
    background-color: #2b3340;
    border: 1px solid #4a5d73;
    border-radius: 3px;
    padding: 3px;
}
QComboBox::drop-down {
    border-left: 1px solid #4a5d73;
}
QComboBox QAbstractItemView {
    background-color: #2b3340;
    border: 1px solid #4a5d73;
    selection-background-color: #3b4759;
}
QDoubleSpinBox {
    background-color: #2b3340;
    border: 1px solid #4a5d73;
    border-radius: 3px;
    padding: 3px;
}
QLabel {
    background-color: transparent;
    border: none;
}
#total_weight_label {
    color: #008cff;
}
#status_label {
    color: #008cff;
    border-top: 1px solid #4a5d73;
}
QScrollArea {
    border: none;
}
QScrollBar:vertical {
    background: #1a1e26;
    width: 12px;
    margin: 0px;
    border-radius: 6px;
}
QScrollBar::handle:vertical {
    background: #2b3340;
    min-height: 20px;
    border-radius: 6px;
}
QScrollBar::handle:vertical:hover {
    background: #4a5d73;
}
QScrollBar::add-line:vertical, QScrollBar::sub-line:vertical {
    background: none;
    height: 0px;
    subcontrol-position: top;
    subcontrol-origin: margin;
}
