stylesheet:
QWidget {
    background-color: #202020;
    color: #e0e0e0;
}
QPushButton {
    background-color: #333333;
    border: 1px solid #555555;
    padding: 5px;
    border-radius: 3px;
}
QPushButton:hover {
    background-color: #444444;
}
QPushButton:pressed {
    background-color: #282828;
}
QPushButton:disabled {
    background-color: #2a2a2a;
    color: #666666;
}
QFrame {
    border: 1px solid #555555;
    border-radius: 4px;
}
QComboBox {
    background-color: #333333;
    border: 1px solid #555555;
    border-radius: 3px;
    padding: 3px;
}
QComboBox::drop-down {
    border-left: 1px solid #555555;
}
QComboBox QAbstractItemView {
    background-color: #333333;
    border: 1px solid #555555;
    selection-background-color: #444444;
}
QDoubleSpinBox {
    background-color: #333333;
    border: 1px solid #555555;
    border-radius: 3px;
    padding: 3px;
}
QLabel {
    background-color: transparent;
    border: none;
}
#total_weight_label {
    color: #fce400;
}
#status_label {
    color: #fce400;
    border-top: 1px solid #555555;
}
QScrollArea {
    border: none;
}
QScrollBar:vertical {
    background: #202020;
    width: 12px;
    margin: 0px;
    border-radius: 6px;
}
QScrollBar::handle:vertical {
    background: #333333;
    min-height: 20px;
    border-radius: 6px;
}
QScrollBar::handle:vertical:hover {
    background: #555555;
}
QScrollBar::add-line:vertical, QScrollBar::sub-line:vertical {
    background: none;
    height: 0px;
    subcontrol-position: top;
    subcontrol-origin: margin;
}
