stylesheet:
QWidget {
    background-color: #2c2f33;
    color: #e0e0e0;
}
QPushButton {
    background-color: #3b3f45;
    border: 2px solid #39c5bb;
    padding: 5px;
    border-radius: 2px;
}
QPushButton:hover {
    background-color: #4a4e54;
}
QPushButton:pressed {
    background-color: #2f3237;
}
QPushButton:disabled {
    background-color: #2f3237;
    color: #666666;
    border: 1px solid #5d646b;
}
QFrame {
    background-color: #3b3f45;
    border: 1px solid #5d646b;
    border-radius: 4px;
}
QComboBox {
    background-color: #2c2f33;
    border: 1px solid #5d646b;
    border-radius: 3px;
    padding: 3px;
}
QComboBox::drop-down {
    border-left: 1px solid #5d646b;
}
QComboBox QAbstractItemView {
    background-color: #2c2f33;
    border: 1px solid #5d646b;
    selection-background-color: #4a4e54;
}
QDoubleSpinBox {
    background-color: #2c2f33;
    border: 1px solid #5d646b;
    border-radius: 3px;
    padding: 3px;
}
QLabel {
    background-color: transparent;
    border: none;
}
#total_weight_label {
    color: #39c5bb;
}
#status_label {
    color: #e0e0e0;
    border-top: 1px solid #5d646b;
}
QScrollArea {
    border: none;
}
QScrollBar:vertical {
    background: #2c2f33;
    width: 12px;
    margin: 0px;
    border-radius: 6px;
}
QScrollBar::handle:vertical {
    background: #5d646b;
    min-height: 20px;
    border-radius: 6px;
}
QScrollBar::handle:vertical:hover {
    background: #7a828a;
}
QScrollBar::add-line:vertical, QScrollBar::sub-line:vertical {
    background: none;
    height: 0px;
    subcontrol-position: top;
    subcontrol-origin: margin;
}
