stylesheet:
QWidget {
    background-color: #f0f8ff;
    color: #000000;
}
QPushButton {
    background-color: #e1eefc;
    border: 1px solid #b0c4de;
    padding: 5px;
    border-radius: 3px;
}
QPushButton:hover {
    background-color: #d6e4f0;
}
QPushButton:pressed {
    background-color: #c0d6e4;
}
QPushButton:disabled {
    background-color: #dcdcdc;
    color: #888888;
}
QFrame {
    border: 1px solid #b0c4de;
    border-radius: 4px;
}
QComboBox {
    background-color: #ffffff;
    border: 1px solid #b0c4de;
    border-radius: 3px;
    padding: 3px;
}
QComboBox::drop-down {
    border-left: 1px solid #b0c4de;
}
QComboBox QAbstractItemView {
    background-color: #ffffff;
    border: 1px solid #b0c4de;
    selection-background-color: #d6e4f0;
}
QDoubleSpinBox {
    background-color: #ffffff;
    border: 1px solid #b0c4de;
    border-radius: 3px;
    padding: 3px;
}
QLabel {
    background-color: transparent;
    border: none;
}
#total_weight_label {
    color: #39c5bb;
}
#status_label {
    color: #005040;
    border-top: 1px solid #b0c4de;
}
QScrollArea {
    border: none;
}
QScrollBar:vertical {
    background: #f0f8ff;
    width: 12px;
    margin: 0px;
    border-radius: 6px;
}
QScrollBar::handle:vertical {
    background: #b0c4de;
    min-height: 20px;
    border-radius: 6px;
}
QScrollBar::handle:vertical:hover {
    background: #a0b4ce;
}
QScrollBar::add-line:vertical, QScrollBar::sub-line:vertical {
    background: none;
    height: 0px;
    subcontrol-position: top;
    subcontrol-origin: margin;
}
