stylesheet:
QWidget {
    background-color: #2A2A2A;
    color: white;
}
QPushButton {
    background-color: #3a3e3b;
    border: 2px solid #FF3C3F;
    padding: 5px;
    border-radius: 2px;
}
QPushButton:hover {
    background-color: #4b4f4d;
}
QPushButton:pressed {
    background-color: #2f3130;
}
QPushButton:disabled {
    background-color: #2f3130;
    color: #666666;
    border: 1px solid #AC4044;
}
QFrame {
    border: 1px solid #AC4044;
    background-color: #AC4044;
    border-radius: 4px;
}
QComboBox {
    background-color: #3a3e3b;
    border: 1px solid #AC4044;
    border-radius: 3px;
    padding: 3px;
}
QComboBox::drop-down {
    border-left: 1px solid #AC4044;
}
QComboBox QAbstractItemView {
    background-color: #3a3e3b;
    border: 1px solid #AC4044;
    selection-background-color: #4b4f4d;
}
QDoubleSpinBox {
    background-color: #3a3e3b;
    border: 1px solid #AC4044;
    border-radius: 3px;
    padding: 3px;
}
QLabel {
    background-color: transparent;
    border: none;
}
#total_weight_label {
    color: #FF3C3F;
}
#status_label {
    color: #75DD45;
    border-top: 1px solid #AC4044;
}
QScrollArea {
    border: none;
}
QScrollBar:vertical {
    background: #2A2C2B;
    width: 12px;
    margin: 0px;
    border-radius: 6px;
}
QScrollBar::handle:vertical {
    background: #AC4044;
    min-height: 20px;
    border-radius: 6px;
}
QScrollBar::handle:vertical:hover {
    background: #c25a5e;
}
QScrollBar::add-line:vertical, QScrollBar::sub-line:vertical {
    background: none;
    height: 0px;
    subcontrol-position: top;
    subcontrol-origin: margin;
}
